// page init
jQuery(function(){
  initSlick();
  initMobileNav();
  initFitVids();
  initPopups();
  initCustomSelect();
  initSameHeight();
  initCustomHover();
});

// add classes on hover/touch
function initCustomHover() {
  jQuery('.discovery-blocks .img-wrap a').touchHover();
}


// align blocks height
function initSameHeight() {
  jQuery('#wrapper').sameHeight({
    elements: '.listing-content-wrap',
    flexible: true,
    multiLine: true
  });

  jQuery('.inquiry-form-holder').sameHeight({
    elements: '.content-left, .content-right',
    flexible: true,
    multiLine: true
  });

  jQuery('.carousel-holder').sameHeight({
    elements: '.carousel, .carousel-article',
    flexible: true,
    multiLine: true
  });
}

// Slick Slider
function initSlick() {
  jQuery('.carousel').slick({
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    adaptiveHeight: true,
    autoplay: true,
    autoplaySpeed: 5000,
    cssEase: 'linear',
    pauseOnHover: false,
    arrows: false,
    draggable: false,
    focusOnSelect: true,
    dots: true
  });

  jQuery('.slideshow').slick({
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    fade: true,
    draggable: false,
    asNavFor: '.slider-nav',

    responsive: [
    {
      breakpoint: 768,
      settings: {
        arrows: true,
        fade: false
      }
    },
    ]
  });

  jQuery('.slider-nav').slick({
    slidesToShow: 3,
    slidesToScroll: 1,
    asNavFor: '.slideshow',
    draggable: false,
    focusOnSelect: true,
    arrows: true,
    responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
      }
    },
    ]
  });

  jQuery('.listings-slider').slick({
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    adaptiveHeight: true,
    autoplay: false,
    autoplaySpeed: 5000,
    cssEase: 'linear',
    pauseOnHover: false,
    arrows: true,
    draggable: false,
    focusOnSelect: true,
    responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
      }
    },
    {
      breakpoint: 768,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      }
    }
    ]
  });
}

// mobile menu init
function initMobileNav() {
  jQuery('body').mobileNav({
    hideOnClickOutside: true,
    menuActiveClass: 'popup-active',
    menuOpener: '.rail-opener',
    menuDrop: '.rail-slider'
  });

  jQuery('body').mobileNav({
    hideOnClickOutside: true,
    menuActiveClass: 'nav-active',
    menuOpener: '.nav-opener',
    menuDrop: '.navigation'
  });
}

// handle flexible video size
function initFitVids() {
  jQuery('#wrapper').fitVids();
}

// popups init
function initPopups() {
  jQuery('.column-holder').contentPopup({
    mode: 'click',
    popup: '.popup-block',
    btnOpen: '.popup-opener',
    btnClose: '.popup-close',
    hideOnClickOutside: true
  });
}

// custom select
function initCustomSelect() {
  jQuery('select').niceSelect();
}

